INSERT INTO xcart_languages SET code='en', name='eml_amazon_pa_pm2be_updated', value='There was a problem with the payment method chosen. You need to update your payment method on <a href="{{url2update_pm}}" target="_blank">this page</a>.<br /><br />Alternatively, you can visit the <a href="https://pay.amazon.com/uk/" target="_blank">Amazon Pay website</a>, look up your order, and update the payment method by following the instructions on the webpage.', topic='E-Mail';
