INSERT INTO xcart_languages VALUES ('en','module_requirements_Amazon_Payments_Advanced', 'The module requires secure connection via HTTPS (http://help.x-cart.com/index.php?title=X-Cart:Configuring_HTTPS).<br> The OpenSSL extension and hash (http://www.php.net/manual/en/function.hash.php) functions should be also available.<br />', 'Modules');
