INSERT INTO xcart_languages VALUES ('en','txt_amazon_pa_configuration_info','<p>Please make sure the following settings are configured properly in your:</p><p><em>&nbsp; </em><a href="https://sellercentral.amazon.com/" target="_blank">Amazon Payments account</a>:</p><p><strong>&nbsp; &nbsp; Merchant URL</strong>&nbsp;field on the <a href="https://sellercentral.amazon.com/gp/pyop/seller/account/settings/user-settings-edit.html" target="_blank">Integration Settings</a> page: <em>{{merchant_url}}</em></p><p><em>&nbsp;&nbsp;</em><a href="https://sellercentral.amazon.com/" target="_blank">Login with Amazon account</a>:</p><p>&nbsp; &nbsp;&nbsp;<strong>Allowed JavaScript Origins</strong>&nbsp;option&nbsp;on the <a href="https://sellercentral.amazon.com/gp/homepage.html" target="_blank">Application settings</a> page:&nbsp;<em>{{allowed_javascript_origins}}</em></p><p><em>&nbsp; &nbsp;&nbsp;</em><strong>Allowed Return URLs</strong>&nbsp;option&nbsp;on the <a href="https://sellercentral.amazon.com/gp/homepage.html" target="_blank">Application settings</a> page:&nbsp;<em>{{allowed_return_urls}}</em></p>','Text');
