REPLACE INTO xcart_languages SET code='en', name='opt_descr_adv_mailchimp_send_carts', value='This option can be used to create an <a href=\"http://kb.mailchimp.com/automation/create-an-abandoned-cart-workflow?utm_source=xcart4&amp;utm_medium=xcart4&amp;utm_campaign=xcart4\" target=\"_blank\">Abandoned Cart workflow</a>, or to save a consumer\'s shopping cart <a href=\"http://developer.mailchimp.com/documentation/mailchimp/guides/getting-started-with-ecommerce/#about-carts-and-orders\" target=\"_blank\">pending a successful Order</a>.<br /><br />Please note that, if this option is enabled, basic information about all the products being added to the shopping cart will be stored on Mailchimp servers.', topic='Options';
REPLACE INTO xcart_languages SET code='en', name='opt_descr_adv_mailchimp_send_orders', value='To use this option, enable <a href=\"http://kb.mailchimp.com/integrations/e-commerce/how-to-use-mailchimp-for-e-commerce#Turn-on-E-Commerce-Link-Tracking\" target=\"_blank\">E-Commerce Link Tracking</a><br /><br />Please note that, if this option is enabled, basic information about all the products being added to the shopping cart will be stored on Mailchimp servers.', topic='Options';
