REPLACE INTO xcart_languages SET code='en', name='module_descr_Segment', value='Segment is a powerful data aggregator, it tracks e-commerce events and sends this info to such apps as Mixpanel, Google Analytics, Localytics, RJ Metrics, SalesForce, HubSpot, Optimizely, Clicky and other data processing tools.<br /><b>Note:</b> This module requires Google Analytics module to be disabled.', topic='Modules';
