REPLACE INTO xcart_languages SET code='en', name='msg_adm_segment_ga_both_modules_are_enabled', value='Your store has the modules \'Google Analytics\' and \'Segment\' enabled at the same time. Simultaneous use of these two modules may <a href=\"https://segment.com/docs/integrations/google-analytics/#getting-started\" target=\"_blank\">cause errors</a>. It is recommended to disable one of the modules to ensure there is no conflict.', topic='Text';
